import React, { Component } from "react";
import * as emailjs from "emailjs-com";

class ContactThree extends Component{
    constructor(props){
        super(props);
        this.state = {
            fname: '',
            email: '',
            subject: '',
            message: '',
            mailSent: false,
            error: null,
        };
        this.handleFormSubmit = this.handleFormSubmit.bind(this);
    }

    handleFormSubmit(e) {
        e.preventDefault();
        const { fname, email, subject, message } = this.state;
        let templateParams = {
          from_name: fname,
          user_mail: email,
          subject: subject,
          message: message,
        };
        emailjs.send(
          "default_service",
          "emailtemplate",
          templateParams,
          "user_FqIXKAIpDQYC1lkU3it2G"
        );
        alert(
          "A form was submitted: " + this.state.name + " // " + this.state.email
        );
    
        this.resetForm();
      }
    
      resetForm() {
        this.setState({
          fname: "",
          email: "",
          subject: "",
          message: "",
          mailSent: true,
          error: null,
        });
    }


    render(){
        return(
            <div className="contact-form--1">
                <div className="container">
                    <div className="row row--35 align-items-start">
                        <div className="col-lg-6 order-2 order-lg-1">
                            <div className="section-title text-left mb--50">
                                <h2 className="title">Hire Me.</h2>
                                <p className="description">I'm allways willing to learn new technologies and get involve in challenging projects. </p>
                               <p> Fill the fields below and press submit to contact me.</p>
                            </div>
                            <div className="form-wrapper">
                                <form onSubmit={this.handleFormSubmit.bind(this)}>
                                    <label htmlFor="item01">
                                        <input
                                            type="text"
                                            name="name"
                                            id="item01"
                                            value={this.state.fname}
                                            onChange={(e)=>{this.setState({fname: e.target.value});}}
                                            placeholder="Your Name *"
                                        />
                                    </label>

                                    <label htmlFor="item02">
                                        <input
                                            type="text"
                                            name="email"
                                            id="item02"
                                            value={this.state.email}
                                            onChange={(e)=>{this.setState({email: e.target.value});}}
                                            placeholder="Your email *"
                                        />
                                    </label>

                                    <label htmlFor="item03">
                                        <input
                                            type="text"
                                            name="subject"
                                            id="item03"
                                            value={this.state.subject}
                                            onChange={(e)=>{this.setState({subject: e.target.value});}}
                                            placeholder="Write a Subject"
                                        />
                                    </label>
                                    <label htmlFor="item04">
                                        <textarea
                                            type="text"
                                            id="item04"
                                            name="message"
                                            value={this.state.message}
                                            onChange={(e)=>{this.setState({message: e.target.value});}}
                                            placeholder="Your Message"
                                        />
                                    </label>
                                    <button className="rn-button-style--2 btn-solid" type="submit" value="submit" name="submit" id="mc-embedded-subscribe">Submit</button>
                                </form>
                            </div>
                        </div>
                        <div className="col-lg-6 order-1 order-lg-2">
                            <div className="thumbnail mb_md--30 mb_sm--30">
                                <img src={`${this.props.contactImages}`} alt="trydo"/>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
export default ContactThree;
